import React from 'react';
import './App.css';
import './index.js'
import firebase from 'firebase';
// import firebaseConfig from './firebase.js';
import { app } from './firebase.js';



class App extends React.Component{
  constructor(props){
    super(props);

    this.myDatabase = app.database().ref().child('people');

    this.state = {
      people: {
        admin: "",
        student: "",
        teacher: ""
      }

    }
  }

  componentDidMount = () => {
    this.myDatabase.on("value", snap => {
      this.setState({
        people:{
          admin: snap.val(),
          student: snap.val(),
          teacher: snap.val()
        }
      });
    });
  }

  render(){
    return(
      <div>
        <h1> the teacher is {this.state.teacher} </h1>
      </div>
    )
  }
}



export default App;
