import React from 'react';
import './App.css';
import './index.js'
import firebase from 'firebase';
import base from './firebase';

class App extends React.Component {
    constructor(props){
        super(props);

        this.addClassInfo = this.addClassInfo.bind(this);
        this.updateClasses = this.updateClasses.bind(this);

        this.state = {
            classes: { }
        };
    }

    addClassInfo = (e) => {
        const classes = {...this.state.classes};
        const classNumber = e.target.value;
        classes[classNumber] = {
            student: "",
            teacher: ""
        }

        this.setState({
            classes
        })
    }

    updateClasses = (e) => {
         const classes = {...this.state.classes};
         classes[e.target.value] = e.target.value; 
         this.setState({
             classes
         });
    }

    componentWillMount = () => {
        this.classRef = base.syncState('classes', {
            context: this,
            state: 'classes'
        });
    }

    componentWillUnmount = () => {
        base.removeBinding(this.classRef);
    }

    render(){
        return(
            <div className="mainClass">
                                 <input type="text" id="textArea" value={this.state.student}  onChange={e=>this.inputStudent(e)}> 
                 </input>

                 <input type="text" id="textArea" value={this.state.student.age}  onChange={e=>this.inputAge(e)}> 
                 </input>

                 <input type="text" id="textArea" value={this.state.student.gender}  onChange={e=>this.inputGender(e)}> 
                </input>

                 <button type="submit" onClick={()=>this.addStudent()}>
                     Add Student
                 </button>

            </div>
        )
    }
}

export default App;




