import React from 'react';
import './App.css';
import './index.js'
// import firebase from 'firebase';
// import firebaseConfig from './firebase.js';
import { app } from './firebase.js';



class App extends React.Component{
  constructor(props){
    super(props);

    // this.app = firebase.initializeApp(firebaseConfig);
    // const preObject = document.getElementById('people');
    this.myDatabase = app.database().ref().child('people');

    this.state = {
      class: "",
      student: "",
      teacher: "",
      admin: ""

    }
  }

  componentDidMount = () => {
    this.myDatabase.on("value", snap => {
      this.setState({
        student: snap.val(),
        teacher: snap.val(),
        admin: snap.val()
      });
    });
  }

  render(){
    return(
      <div>
        <h1> the teacher is {this.state.teacher} </h1>
      </div>
    )
  }
}



export default App;
