import React from 'react';
import './App.css';
import './index.js'
import firebase from 'firebase';
// import firebaseConfig from './firebase.js';
import app  from './firebase';



class App extends React.Component{
  constructor(props){
    super(props);

    this.myDatabase = app.database().ref().child('teacher');
    this.adminData = app.database().ref("school").child('admin');
    this.admin = this.adminData.child('name');

    this.state = {
      people: {
        class: "",
        admin: "",
        student: "",
        teacher: ""
      }

    }
  }

  componentDidMount = (snap) => {
    this.admin.on("value", snap => {
      this.setState({
        teacher: snap.val(),
        student: snap.val()

        // people:{
        //   admin: snap.val(),
        //   student: snap.val(),
        //   teacher: snap.val()
        // }
      });
    });
  }

  render(){
    return(
      <div>
        <p> 
          the teacher is {this.state.teacher}
          hello i am {this.state.people.name}
          {this.state.student}
        </p>
      </div>
    )
  }
}



export default App;
