import React from 'react';
import './App.css';
import './index.js'
import firebase from 'firebase';
import app  from './firebase';

class Student extends React.Component {
    constructor(props){
        super(props);

        this.classData = app.database();

        this.state = {
            name: "",
            age: "",
            grade: "",
            gender: ""
        }

        this.inputStudent = this.inputStudent.bind(this);
        this.inputAge = this.inputAge.bind(this);
        this.inputGender = this.inputGender.bind(this);
        this.addStudent = this.addStudent.bind(this);
    }

    // Input the student's information in the textbox
    inputStudent = (e) => {
        let studentVal = e.target.value;
        this.setState({
            student: studentVal
        })
    }

    inputAge = (e) => {
        let studentAge = e.target.value;
        this.setState({
            student: [{
                age: studentAge
            }]
        })
    }
    inputGender = (e) => {
        let studentGender = e.target.value;
        this.setState({
            student: [{
                gender: studentGender
            }]
        })
    }

    // Add the student's information to the database
    addStudent = (e) => {

        let newStudent = this.state.student;

        if(newStudent !== "" && newStudent.age !== "" && newStudent.gender !== ""){
            this.schoolClass.push(this.state.student);

            this.setState({
                name: "",
                age: "",
                grade: "",
                gender: ""
            })

        }

    }

    render(){
        return(
            <div className="StudentMain"> 
            <section className="add-item">
            <form>
                <input type="text" name="name" placeholder="Student Name" onChange={this.handleChange} value={this.state.name} />
                <input type="text" name="age" placeholder="Student Age" onChange={this.handleChange} value={this.state.age} />
                <input type="text" name="grade" placeholder="Student Grade" onChange={this.handleChange} value={this.state.grade} />
                <input type="text" name="gender" placeholder="Student Gender" onChange={this.handleChange} value={this.state.gender} />
                <button>Add Item</button>
            </form>
            </section>
            </div>
        )
    }
}

export default Student;
